import React, { Component } from 'react'
import axios from 'axios'
import { Link } from 'react-router-dom'

export default class FacturasLista extends Component {

    state = {
        idfactura: [] 
    }
    async componentDidMount() {
        this.getFacturas();
    }

    async getFacturas() {
        const res = await axios.get('http://localhost:4000/api/facturas');
        this.setState({ facturas: res.data });
    }
    deleteFacturas = async (facturaId) => {
        const response = window.confirm('Está seguro de eliminar el registro?');
        if (response) {
            await axios.delete('http://localhost:4000/api/facturas/' + facturaId);
            this.getFacturas();
        }
    }


    render() {
        return (
            <div>
                <div>
                <div className="row">
                    <div className="col-md-3">
                        <div className="table-dark">
                            <h3>Crear Producto</h3>
                        </div>
                        <div className="card card-body">
                            <form on onSubmit={this.onSubmit}>
                                <div className="form-group">

                                    <label for="idproducto">ID Producto</label>
                                    <input type="text" className="form-control" placeholder="Código del producto"
                                        value={this.state.idproducto}
                                        id="idproducto"
                                        onChange={this.onInputChage}
                                        required />
                                </div>

                                <div className="form-group">

                                    <label for="nombreproducto">Nombre Producto</label>
                                    <input type="text" className="form-control" placeholder="Nombre del Producto"
                                        value={this.state.nombreproducto}
                                        id="nombreproducto"
                                        onChange={this.onInputChage}
                                        required />
                                </div>



                                <div class="form-group">
                                    <label for="costo">Valor de Venta</label>
                                    <input type="text" class="form-control"
                                        value={this.state.costo}
                                        id="costo" placeholder="Ingrese el valor de venta"
                                        onChange={this.onInputChage}
                                        required />
                                </div>

                                <button type="submit" className="btn btn-primary">
                                    Grabar
                                </button>
                            </form>
                        </div>
                    </div>
                    <div className="-col-md-9">
                        <table className="table table-bordered">
                            <thead className="table-dark">
                                <tr >
                                    <th>ID Producto</th>
                                    <th>Nombre Producto</th>
                                    <th>Costo</th>
                                    <th>Acciones</th>
                                </tr>
                            </thead>
                            <tbody className="table-secondary">
                                {
                                    this.state.productos.map((dato) => (
                                        <tr key={dato._id}>
                                            <td >{dato.idproducto}</td>
                                            <td>{dato.nombreProducto}</td>
                                            <td>{dato.costo}</td>
                                            <td>
                                                <button className="btn btn-primary"
                                                key={dato._id} onClick={() => this.updateProductos(dato._id)}>
                                                Editar</button>
                                                {" "}
                                                <button className="btn btn-warning"
                                                    key={dato._id} onClick={() => this.deleteProductos(dato._id)}>
                                                        Borrar</button>
                                            </td>
                                        </tr>
                                    ))}
                            </tbody>

                        </table>
                    </div>
                </div>
            </div>
                
            </div>
        )
    }
}
