import React, { Component } from 'react'
import axios from 'axios'
import { Link } from 'react-router-dom'

export default class FacturasLista extends Component {

    state = {
        idfactura: [] 
    }
    async componentDidMount() {
        this.getFacturas();
    }

    async getFacturas() {
        const res = await axios.get('http://localhost:4000/api/facturas');
        this.setState({ facturas: res.data });
    }
    deleteFacturas = async (facturaId) => {
        const response = window.confirm('Está seguro de eliminar el registro?');
        if (response) {
            await axios.delete('http://localhost:4000/api/facturas/' + facturaId);
            this.getFacturas();
        }
    }


    render() {
        return (
            <div>
                <div className="row">
                    
                    <div className="-col-md-9">
                        <table className="table table-bordered">
                            <thead className="table-dark">
                                <tr >
                                    <th>ID Factura</th>
                                    <th>identificación</th>
                                    <th>Nombre</th>
                                    <th>Ciudad</th>
                                    <th>Telefono</th>
                                    <th>Total Factura</th>
                                    <th>Vendedor</th>
                                </tr>
                            </thead>
                            <tbody className="table-secondary">
                                {
                                    this.state.facturas.map((dato) => (
                                        <tr key={dato._id}>
                                            <td >{dato.idfactura}</td>
                                            <td>{dato.ide}</td>
                                            <td>{dato.costo}</td>
                                            <td>
                                                <button className="btn btn-primary"
                                                key={dato._id} onClick={() => this.updateProductos(dato._id)}>
                                                Editar</button>
                                                {" "}
                                                <button className="btn btn-warning"
                                                    key={dato._id} onClick={() => this.deleteProductos(dato._id)}>
                                                        Borrar</button>
                                            </td>
                                        </tr>
                                    ))}
                            </tbody>

                        </table>
                    </div>
                </div>
            </div>
        )
    }
}
