import React, { Component } from 'react'
import axios from 'axios'

export default class ProductosCrear extends Component {

    state = {
        productos: [],
        idproducto: '',
        nombreProducto: '',
        costo: ''   
    }

    async componentDidMount() {
        this.getProductos();
    }

    async getProductos() {
        const res = await axios.get('http://localhost:4000/api/productos');
        this.setState({ productos: res.data });
    }

    onInputChage = (e) => {
     this.setState({
            [e.target.id]: e.target.value
        })
    }

    onSubmit = async (e) => {
        e.preventDefault();
        const newProducto = {
            idproducto: this.state.idproducto,
            nombreProducto: this.state.nombreproducto,
            costo: this.state.costo
        };
        await axios.post('http://localhost:4000/api/productos', newProducto)
        this.setState({ idproducto: '' });
        this.setState({ nombreProducto: '' });
        this.setState({ costo: '' })
        this.getProductos();
    }

    deleteProductos = async (userId) => {
        const response = window.confirm('Est√° seguro de eliminar el registro?');
        if (response) {
            await axios.delete('http://localhost:4000/api/productos/' + userId);
            this.getProductos();
        }
    }


    render() {
        return (
            <div>
                <div className="row">
                    <div className="col-md-3">
                        <div className="table-dark">
                            <h3>Crear Producto</h3>
                        </div>
                        <div className="card card-body">
                            <form on onSubmit={this.onSubmit}>
                                <div className="form-group">

                                    <label for="identificacion">Identificaci√≥n</label>
                                    <input type="text" className="form-control" placeholder="Ingrese la identificaci√≥n"
                                        value={this.state.identificacion}
                                        id="identificacion"
                                        onChange={this.onInputChage}
                                        required />
                                </div>

                                <div class="form-group">
                                    <label for="login">Login o UserName</label>
                                    <input type="text" class="form-control"
                                        value={this.state.login}
                                        id="login" placeholder="Ingrese el login"
                                        onChange={this.onInputChage}
                                        required />
                                </div>

                                <div class="form-group">
                                    <label for="nombres">Nombre Completo</label>
                                    <input type="text" class="form-control"
                                        value={this.state.nombres}
                                        id="nombres" placeholder="Ingrese el login"
                                        onChange={this.onInputChage}
                                        required />
                                </div>

                                <div class="form-group">
                                    <label for="clave">Password</label>
                                    <input type="password" class="form-control"
                                        value={this.state.clave}
                                        id="clave" placeholder="Password"
                                        onChange={this.onInputChage}
                                        required />
                                </div>

                                <div class="form-group">
                                    <label for="email">E-mail</label>
                                    <input type="email" class="form-control"
                                        value={this.state.email}
                                        id="email" aria-describedby="emailHelp" placeholder="Ingrese Email"
                                        onChange={this.onInputChage} />

                                    <small id="emailHelp" class="form-text text-muted">Nunca comparta su usuario o email con nadie.</small>
                                </div>


                                <div class="form-group">
                                    <label for="perfil">Perfil</label>
                                    <select class="form-control"
                                        id="perfil"
                                        onChange={this.onInputChage}>
                                        <option selected>Seleccione Perfil...</option>
                                        <option>Administrador</option>
                                        <option>Vendedor</option>
                                    </select>
                                </div>


                                <button type="submit" className="btn btn-primary">
                                    Grabar
                                </button>
                            </form>
                        </div>
                    </div>
                    <div className="-col-md-9">
                        <table className="table table-bordered">
                            <thead className="table-dark">
                                <tr >
                                    <th>Identificaci√≥n</th>
                                    <th>Login</th>
                                    <th>Nombre Completo</th>
                                    <th>E-Mail</th>
                                    <th>Perfil</th>
                                    <th>Acciones</th>
                                </tr>
                            </thead>
                            <tbody className="table-secondary">
                                {
                                    this.state.users.map((dato) => (
                                        <tr key={dato._id}>
                                            <td >{dato.identificacion}</td>
                                            <td>{dato.login}</td>
                                            <td>{dato.nombres}</td>
                                            <td>{dato.email}</td>
                                            <td>{dato.perfil}</td>
                                            <td>
                                                <button className="btn btn-primary"
                                                    >Editar</button>
                                                    {" "}
                                                <button className="btn btn-warning"
                                                    key={dato._id} onClick={() => this.deleteUser(dato._id)}>Borrar</button>
                                            </td>
                                        </tr>
                                    ))}
                            </tbody>

                        </table>
                    </div>
                </div>
            </div>
        )
    }
}
