import React, { Component } from 'react'
import axios from 'axios'
export default class UsuariosCrear extends Component {

    state = {
        users: [],
        identificacion:''
    }
    async componentDidMount() {
       this.getUser();
    }

     getUser=async()=>{
        const res = await axios.get('http://localhost:4000/api/users');
        this.setState({users:res.data});
        console.log(this.state.users);
    }

    onChangeUserName=(e)=>{
        this.setState({
            identificacion: e.target.value
        })
    }

    onSubmit= async  e=>{
        e.preventDefault();
        await axios.post('http://localhost:4000/api/users',{
            identificacion: this.state.identificacion
        })
        this.setState({identificacion:''});
        this.getUser();
    }

    deleteUser = async (userId) => {
        const response = window.confirm('are you sure you want to delete it?');
        if (response) {
            await axios.delete('http://localhost:4000/api/users/' + userId);
            this.getUsers();
        }
    }


    
    render() {
        return (
            <div>
                <div className="row">
                    <div className="col-md-4">
                        <div className="card card-body">
                            <h3>Crear Nuevo Usuario</h3>
                            <form on onSubmit={this.onSubmit}>
                                <div className="form-group">
                                <label for="identificacion">Identificaci√≥n</label>
                                    <input type="text" className="form-control" p
                                    value={this.state.identificacion}
                                    onChange={this.onChangeUserName}/>
                                </div>
                                <button type="submit"className="btn btn-primary">
                                    Grabar
                                </button>
                            </form>
                        </div>
                    </div>
                    <div className="-col-md-8">
                        <ul className="list-group">
                            {
                                this.state.users.map(usuario => (
                                    <li className="list-group-item list-group-item-action" 
                                    key={usuario._id}
                                    onDoubleClick={()=>this.deleteUser(usuario._id)}
                                    >
                                        {usuario.nombres, usuario.identificacion}
                                    </li>)
                                )
                            }
                        </ul>
                    </div>
                </div>
            </div>
        )
    }
}
