import React, { Component } from 'react'
import axios from 'axios'

export default class ProductosLista extends Component {

    state = {
        productos: [],
        idproducto: '',
        nombreProducto: '',
        costo: ''   
    }

    async componentDidMount() {
        this.getProductos();
    }

    async getProductos() {
        const res = await axios.get('http://localhost:4000/api/productos');
        this.setState({ productos: res.data });
    }

    onInputChage = (e) => {
     this.setState({
            [e.target.id]: e.target.value
        })
    }

    onSubmit = async (e) => {
        e.preventDefault();
        const newProducto = {
            idproducto: this.state.idproducto,
            nombreProducto: this.state.nombreproducto,
            costo: this.state.costo
        };
        await axios.post('http://localhost:4000/api/productos', newProducto)
        this.setState({ idproducto: '' });
        this.setState({ nombreProducto: '' });
        this.setState({ costo: '' })
        this.getProductos();
    }

    deleteProductos = async (userId) => {
        const response = window.confirm('Está seguro de eliminar el registro?');
        if (response) {
            await axios.delete('http://localhost:4000/api/productos/' + userId);
            this.getProductos();
        }
    }





    render() {
        return (
            <div>
                PRODUCTOS LISTA
            </div>
        )
    }
}
