import React, { Component } from 'react'
import axios from 'axios'
import { Link } from 'react-router-dom'

export default class FacturasLista extends Component {

    state = {
        idfactura: [] 
    }
    async componentDidMount() {
        this.getFacturas();
    }

    async getFacturas() {
        const res = await axios.get('http://localhost:4000/api/facturas');
        this.setState({ facturas: res.data });
    }
    deleteFacturas = async (facturaId) => {
        const response = window.confirm('Está seguro de eliminar el registro?');
        if (response) {
            await axios.delete('http://localhost:4000/api/facturas/' + facturaId);
            this.getFacturas();
        }
    }


    render() {
        return (
            <div className="row">
            {
                this.state.facturas.map(factura => (
                    <div className="col-md-4 p-2" key={factura._id}>
                        <div className="card">
                            <div className="card-header d-flex justify-content-between">
                                <h5>{factura.idfactura}</h5>
                                <Link to={"/edit/" + factura._id} className="btn btn-secondary">
                                    <i className="material-icons">
                                        border_color</i>
                                </Link>
                            </div>
                            <div className="card-body">
                                <p>
                                    {factura.identificacion}
                                </p>
                                <p>
                                    Nombre: {factura.nombre}
                                </p>
                                
                            </div>
                            <div className="card-footer">
                                <button className="btn btn-danger" onClick={() => this.deleteFacturas(factura._id)}>
                                    Delete
                                </button>
                            </div>
                        </div>
                    </div>
                ))
            }
        </div>
        )
    }
}
