import React, { Component } from 'react'
import axios from 'axios'
export default class UsuariosCrear extends Component {

    state = {
        usersRoute: []
    }
    async componentDidMount() {
        const res = await axios.get('http://localhost:4000/api/users');
        this.setState({usersRoute:res.data});
        console.log(this.state.usersRoute);
    }

    onChangeUserName=(e)=>{
        this.setState({
            identificacion: e.target.value
        })
       
    }

    
    render() {
        return (
            <div>
                <div className="row">
                    <div className="col-md-4">
                        <div className="card card-body">
                            <h3>Crear Nuevo Usuario</h3>
                            <form>
                                <div className="form-group">
                                    <input type="text" className="form-control"onChange={this.onChangeUserName}/>
                                </div>
                            </form>
                        </div>
                    </div>
                    <div className="-col-md-8">
                        <ul className="list-group">
                            {
                                this.state.users.map(users => (
                                    <li className="list-group-item list-group-item-action" key={users.id}>
                                        {users.nombres}
                                    </li>)
                                )
                            }
                        </ul>
                    </div>
                </div>
            </div>
        )
    }
}
