import React, { Component } from 'react'
import axios from 'axios'
import { useAuthState } from "react-firebase-hooks/auth";
import { useHistory } from "react-router";
import { getAuth } from "firebase/auth";
import { async } from '@firebase/util';

export default class FacturasCrear extends Component {

    state={
        users:[]
    }

    state = {
        idfactura: '',
        fecha: new Date(),
        identificacion: '',
        nombre: '',
        ciudad:'',
        telefono:'',
        idproducto:'',
        cantidad:'',
        precio:'',
        vendedores: [],
        editing: false,
        _id: ''
    }

    async componentDidMount() {
        const res = await axios.get('http://localhost:4000/api/users');
        if (res.data.length > 0) {
            this.setState({
                users: res.data.map(user => user.nombres),
                userSelected: res.data[0].nombres
            })
        }
        if (this.props.match.params.id) {
            console.log(this.props.match.params.id)
            const res = await axios.get('http://localhost:4000/api/facturas/' + this.props.match.params.id);
            console.log(res.data)
            this.setState({
                idfactura: res.data.idfactura,
                fecha: new Date(res.data.fecha),
                identificacion: res.data.identificacion,
                nombre:res.data,
                _id: res.data._id,
                editing: true
            });
        }
    }

    onSubmit=async(e)=>{
        e.preventDefaul();
    }
    onInptuChange = e =>{

    }


    render() {
        return (
            <div className="col-md-6 offset-md-3">
                <div className="card card-body">
                    <h4>CREAR FACTURAS </h4>
                    {/** SELECCIONE VENDEDOR */}
                <div className="form-group">
                    <select className="form-control" 
                    name="userSelected" onChange>
                        {
                            this.state.users.map(user=>
                                <option key={user} value={user} >
                                    {user.nombres}
                                </option>)
                        }

                    </select>
                </div>
                    <form onSubmit={this.onSubmit}>

                        <button type="submit" className="btn btn-primary">
                            Grabar Factura
                        </button>

                    </form>
                </div>
                
            </div>
        )
    }
}
