import React, { Component } from 'react'
import axios from 'axios'
export default class UsuariosCrear extends Component {

    state = {
        users: [],
        identificacion: '',
        login: '',
        nombres: '',
        clave: '',
        perfil: ''
    }
    async componentDidMount() {
        this.getUser();
    }

    getUser = async () => {
        const res = await axios.get('http://localhost:4000/api/users');
        this.setState({ users: res.data });
        console.log(this.state.users);
    }

    onChangeUserName = (e) => {
        this.setState({
            identificacion: e.target.value
        })
    }

    onSubmit = async e => {
        e.preventDefault();
        await axios.post('http://localhost:4000/api/users', {
            identificacion: this.state.identificacion
        })
        this.setState({ identificacion: '' });
        this.getUser();
    }

    deleteUser = async (userId) => {
        const response = window.confirm('are you sure you want to delete it?');
        if (response) {
            await axios.delete('http://localhost:4000/api/users/' + userId);
            this.getUsers();
        }
    }



    render() {
        return (
            <div>
                <div className="row">
                    <div className="col-md-4">
                        <div className="card card-body">
                            <h3>Crear Nuevo Usuario</h3>
                            <form on onSubmit={this.onSubmit}>
                                <div className="form-group">
                                    <label for="identificacion">Identificación</label>
                                    <input type="text" className="form-control" placeholder="Ingrese la identificación"
                                        value={this.state.identificacion}
                                        onChange={this.onChangeUserName} />
                                </div>

                                <div class="form-group">
                                    <label for="login">Login o UserName</label>
                                    <input type="text" class="form-control" id="login" placeholder="Ingrese el login"
                                        value={this.state.login}
                                        onchange={this.onChangeUserName} />
                                </div>

                                <div class="form-group">
                                    <label for="nombre">Nombre Completo</label>
                                    <input type="text" class="form-control" id="nombre" placeholder="Nombres y Apellidos"
                                        value={this.state.nombres}
                                        onchange={this.onChangeUserName} />
                                </div>

                                <div class="form-group">
                                    <label for="clave">Password</label>
                                    <input type="password" class="form-control" id="clave" placeholder="Password" />
                                </div>

                                <div class="form-group">
                                    <label for="email">E-mail</label>
                                    <input type="email" class="form-control" id="email" aria-describedby="emailHelp" placeholder="Ingrese Email" />
                                    <small id="emailHelp" class="form-text text-muted">Nunca comparta su usuario o email con nadie.</small>
                                </div>


                                <div class="form-group">
                                    <label for="perfil">Perfil</label>
                                    <select id="perfil" class="form-control">
                                        <option selected>Seleccione Perfil...</option>
                                        <option>Administrador</option>
                                        <option>Vendedor</option>
                                    </select>
                                </div>


                                <button type="submit" className="btn btn-primary">
                                    Grabar
                                </button>
                            </form>
                        </div>
                    </div>
                    <div className="-col-md-8">
                        <table className="table">
                            <thead>
                                <tr className="table-dark">
                                    <th>Identificación</th>
                                    <th>Login</th>
                                    <th>Nombre Completo</th>
                                    <th>E-Mail</th>
                                    <th>Clave</th>
                                    <th>Perfil</th>
                                    <th>Acciones</th>
                                </tr>
                            </thead>
                            <tbody>
                                {
                                this.state.users.map((dato) => (
                                    <tr key={dato.id}>
                                        <td>{dato.identificacion}</td>
                                        <td>{dato.login}</td>
                                        <td>{dato.nombres}</td>
                                        <td>{dato.email}</td>
                                        <td>{dato.clave}</td>
                                        <td>{dato.perfil}</td>
                                        <td>{dato.perfil}</td>
                                    </tr>
                                ))}
                            </tbody>

                        </table>
                    </div>
                </div>
            </div>
        )
    }
}
