import React, { Component } from 'react'
import axios from 'axios'
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';


export default class FacturasCrear extends Component {


    state = {
        idfactura: '',
        fecha: new Date(),
        identificacion: '',
        nombre: '',
        ciudad: '',
        telefono: '',
        idproducto: '',
        cantidad: '',
        precio: '',
        userSelected:'',
        users: [],
        editing: false,
        _id: ''
    }

    async componentDidMount() {
        const res = await axios.get('http://localhost:4000/api/users');
        if (res.data.length > 0) {
            this.setState({
                users: res.data.map(user => user.nombres),
                userSelected: res.data[0].nombres
            })
        }
        if (this.props.match.params.id) {
            console.log(this.props.match.params.id)
            const res = await axios.get('http://localhost:4000/api/facturas/' + this.props.match.params.id);
            console.log(res.data)
            this.setState({
                idfactura: res.data.idfactura,
                fecha: new Date(res.data.fecha),
                identificacion: res.data.identificacion,
                nombre: res.data,
                _id: res.data._id,
                editing: true
            });
        }
    }



    onSubmit = async (e) => {
        e.preventDefaul();
        if (this.state.editing) {
            const updatedFactura = {
                idfactura: this.state.idfactura,
                fecha: this.state.fecha,
                identificacion: this.state.identificacion,
                nombre: this.state.nombre,
                ciudad:this.state.ciudad,
                telefono:this.state.telefono,
                idproducto:this.state.idproducto,
                cantidad:this.state.cantidad,
                precio:this.state.precio

            };
            await axios.put('http://localhost:4000/api/facturas/' + this.state._id, updatedFactura);
        } else {
            const newFactura = {
                idfactura: this.state.idfactura,
                fecha: this.state.fecha,
                identificacion: this.state.identificacion,
                nombre: this.state.nombre,
                ciudad:this.state.ciudad,
                telefono:this.state.telefono,
                idproducto:this.state.idproducto,
                cantidad:this.state.cantidad,
                precio:this.state.precio
            };
            axios.post('http://localhost:4000/api/facturas', newFactura);
        }
        window.location.href = '/';

    }

    onInptuChange = e => {
        this.setState({
            [e.target.name]: e.target.value
        })
    }

    onChangeDate = date => {
        this.setState({ date });
    }


    render() {
        return (
            <div className="col-md-12">
                <div className="card card-body">
                    <h4>CREAR FACTURAS </h4>
                    {/** SELECCIONE VENDEDOR */}
                    <div className="form-group">
                    <label for="factura">Vendedor</label>
                        <select className="form-control"
                        value={this.state.userSelected}
                        onChange={this.onInptuChange}
                            name="userSelected" required>
                            {
                                this.state.users.map(user => (
                                    <option key={user} value={user}>
                                        {user}
                                    </option>
                                ))
                            }

                        </select>
                    </div>

                    <div className="form-group">
                        <label for="factura">Número Factura</label>
                        <input type="text" className="form-control" placeholder="Ingrese numero de factura"
                             onChange={this.state.idfactura}
                             value={this.state.idfactura}
                            id="factura"
                            required />
                    </div>

                    <div class="form-group">
                        <label for="fecha">Fecha</label>
                        <DatePicker
                        className="form-form-control"selected={this.state.fecha} onChange={this.onChange}/>
                    </div>

                    <div class="form-group">
                        <label for="identificacion">identificacion</label>
                        <input type="text" class="form-control"

                            id="identificacion"
                            required />
                    </div>

                    <div class="form-group">
                        <label for="nombre">Nombre Cliente</label>
                        <input type="text" class="form-control"
                        
                            id="nombre"
                            required />
                    </div>

                    <div class="form-group">
                        <label for="ciudad">Ciudad</label>
                        <input type="text" class="form-control"

                            id="ciudad"
                            required />
                    </div>

                    <div class="form-group">
                        <label for="telefono">Teléfono</label>
                        <input type="text" class="form-control"

                            id="telefono"
                            required />
                    </div>

                    <div class="form-group">
                        <label for="idproducto">ID Producto</label>
                        <input type="text" class="form-control"

                            id="idproducto"
                            required />
                    </div>

                    <div class="form-group">
                        <label for="cantidad">Cantidad</label>
                        <input type="text" class="form-control"

                            id="cantidad"
                            required />
                    </div>

                    <div class="form-group">
                        <label for="precio">Precio de venta</label>
                        <input type="text" class="form-control"

                            id="precio"
                            required />
                    </div>


                    <form onSubmit={this.onSubmit}>

                        <button type="submit" className="btn btn-primary">
                            Grabar Factura
                        </button>

                    </form>
                </div>

            </div>
        )
    }
}
